import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import MainLayout from '../components/MainLayout.tsx';
import StreakChart from '../components/StreakChart.tsx';

interface ReadingEvent {
  id: number;
  post_id: string;
  created_at: string;
}

interface AdminMetric {
  email: string;
  currentStreak: number;
  maxStreak: number;
}

const Dashboard = () => {
  const userEmail = localStorage.getItem('userEmail') || '';
  const [userData, setUserData] = useState<{
    email: string;
    isAdmin: boolean;
    streak: number;
    maxStreak: number;
    streakHistory: number[];
    events: ReadingEvent[];
  } | null>(null);

  const [adminMetrics, setAdminMetrics] = useState<AdminMetric[]>([]); // üëà Estado adicionado
  const [error, setError] = useState<string | null>(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const email = localStorage.getItem('userEmail');
        if (!email) {
          navigate('/', { replace: true });
          return;
        }

        // 1. Buscar dados b√°sicos do usu√°rio
        const userResponse = await axios.get('http://localhost:3001/api/user/stats', {
          params: { email }
        });
        setUserData(userResponse.data);

        // 2. Se for admin, buscar m√©tricas separadamente
        if (userResponse.data.isAdmin) {
          try {
            const metricsResponse = await axios.get('http://localhost:3001/api/admin/metrics'
            );
            setAdminMetrics(metricsResponse.data);
          } catch (metricsError) {
            console.error('Erro nas m√©tricas admin:', metricsError);
            setError('N√£o foi poss√≠vel carregar m√©tricas administrativas');
          }
        }

      } catch (mainError) {
        setError('Erro ao carregar dados do usu√°rio');
        console.error(mainError);
      }
    };

    fetchData();
  }, [navigate]);

  if (error) {
    return (
      <MainLayout userEmail={userEmail}>
        <div className="error-container">
          <p>{error}</p>
          <button onClick={() => navigate('/')}>Voltar ao Login</button>
        </div>
      </MainLayout>
    );
  }

  if (!userData) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Carregando suas estat√≠sticas...</p>
      </div>
    );
  }

  return (
    <MainLayout userEmail={userData.email}>
      <div className="dashboard container">

        {/* Conte√∫do para usu√°rios comuns */}
        {!userData.isAdmin && (
          <>
            <h1>Bem-vindo, {userData.email}</h1>

            <div className="streak-metrics">
              <div className="metric-box">
                <h3>Streak Atual</h3>
                <div className="metric-value">{userData.streak} üî•</div>
              </div>

              <div className="metric-box">
                <h3>Maior Streak</h3>
                <div className="metric-value">{userData.maxStreak} üèÜ</div>
              </div>

              <div className="chart-container">
                <StreakChart streakHistory={userData.streakHistory} /> 
              </div>
            </div>

            <div className="history">
              <h3>Hist√≥rico de Leituras</h3>
              <ul>
                {userData.events.map((event) => (
                  <li key={event.id}>
                    {new Date(event.created_at).toLocaleDateString()} - Edi√ß√£o #{event.post_id}
                  </li>
                ))}
              </ul>
            </div>
          </>
        )}

        {/* Conte√∫do para admin */}
        {userData.isAdmin && (
          <div className="admin-panel">
            <h2>Painel Administrativo</h2>

            <div className="admin-metrics">
              <h3>Ranking de Leitores</h3>
              <table>
                <thead>
                  <tr>
                    <th>Usu√°rio</th>
                    <th>Streak Atual</th>
                    <th>Maior Streak</th>
                  </tr>
                </thead>
                <tbody>
                  {adminMetrics.map((user, index) => (
                    <tr key={index}>
                      <td>{user.email}</td>
                      <td>{user.currentStreak}</td>
                      <td>{user.maxStreak}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    </MainLayout>
  );
};

export default Dashboard;
