import React from 'react';
import { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import MainLayout from '../components/MainLayout.tsx';

interface ReadingEvent {
  id: number;
  post_id: string;
  created_at: string;
}

const Dashboard = () => {
  const userEmail = localStorage.getItem('userEmail') || '';
  const [userData, setUserData] = useState<{
    email: string;
    isAdmin: boolean;
    streak: number;
    events: ReadingEvent[];
  } | null>(null);
  const navigate = useNavigate();

  console.log('data', userData);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const email = localStorage.getItem('userEmail');
        console.log('Email do localStorage:', email);

        if (!email) {
          console.error('Email nÃ£o encontrado');
          // ðŸ‘‡ Use replace para evitar loop de navegaÃ§Ã£o
          navigate('/', { replace: true });
          return;
        }

        const response = await axios.get('http://localhost:3001/api/user/stats', {
          params: { email }
        });
        console.log('Resposta da API:', response.data);

        // ðŸ‘‡ ForÃ§ar tipagem para evitar erros de estrutura
        if (!response.data || !response.data.email) {
          throw new Error('Dados invÃ¡lidos');
        }

        setUserData(response.data);

      } catch (error) {
        console.error('Erro ao buscar dados:', error);
        // ðŸ‘‡ NÃ£o navegue imediatamente; mostre feedback
        // navigate('/', { replace: true });
      }
    };

    fetchData();
  }, [navigate]);
  console.log('tres', userData);
  console.log('data2',userEmail);

  if (!userData) return (
    <div className="loading-container">
      <div className="spinner"></div>
      <p>Carregando suas estatÃ­sticas...</p>
    </div>
  );

  return (
    <MainLayout userEmail={userData.email}>
    <div className="dashboard">
      <h1>Bem-vindo, {userData.email}</h1>
      <div className="streak-container">
        <h2>Seu Streak: {userData.streak} dias ðŸ”¥</h2>
        <p>Mantenha a sequÃªncia para ganhar recompensas!</p>
      </div>

      <div className="history">
        <h3>HistÃ³rico de Leituras</h3>
        <ul>
          {userData.events.map((event) => (
            <li key={event.id}>
              {new Date(event.created_at).toLocaleDateString()} - EdiÃ§Ã£o #{event.post_id}
            </li>
          ))}
        </ul>
      </div>

      {userData.isAdmin && (
        <div className="admin-panel">
          <h2>Painel Administrativo</h2>
          {/* Se der tempo lembrar de colocar os grÃ¡ficos e as mÃ©ticas aqui */}
        </div>
      )}

          {/* <button
              onClick={() => {
                  localStorage.removeItem('token');
                  window.location.href = '/';
              }}
          >
              Sair
          </button> */}
      </div>
      </MainLayout>
  );
};

export default Dashboard;
