import React from 'react';
import { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

interface ReadingEvent {
  id: number;
  post_id: string;
  created_at: string;
}

const Dashboard = () => {
  const [userData, setUserData] = useState<{
    email: string;
    isAdmin: boolean;
    streak: number;
    events: ReadingEvent[];
  } | null>(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/');
          return;
        }

        const response = await axios.get('http://localhost:3001/api/user/stats', {
          headers: { Authorization: `Bearer ${token}` }
        });

        setUserData(response.data);
      } catch (error) {
        navigate('/');
      }
    };

    fetchData();
  }, [navigate]);

  if (!userData) return <div>Carregando...</div>;

  return (
    <div className="dashboard">
      <h1>Bem-vindo, {userData.email}</h1>
      <div className="streak-container">
        <h2>Seu Streak: {userData.streak} dias üî•</h2>
        <p>Mantenha a sequ√™ncia para ganhar recompensas!</p>
      </div>

      <div className="history">
        <h3>Hist√≥rico de Leituras</h3>
        <ul>
          {userData.events.map((event) => (
            <li key={event.id}>
              {new Date(event.created_at).toLocaleDateString()} - Edi√ß√£o #{event.post_id}
            </li>
          ))}
        </ul>
      </div>

      {userData.isAdmin && (
        <div className="admin-panel">
          <h2>Painel Administrativo</h2>
          {/* Se der tempo lembrar de colocar os gr√°ficos e as m√©ticas aqui */}
        </div>
      )}

          <button
              onClick={() => {
                  localStorage.removeItem('token');
                  window.location.href = '/';
              }}
          >
              Sair
          </button>
      </div>
  );
};

export default Dashboard;
